<script>
	export default{
		data(){
			return {
				isBind:'9',
				isClassify:'-1',//选择类目
				PersonOrCom:'-1',//审核
				chooseTemplate:'-1',//是否选择模板
				searchTemplate:'-1',//搜索、获取货品模板
				goodsInfo:'-1',//填写商品信息
				comCheck:'-1',//机器审核
				personCheck:'-1'//人工审核


			}
		},
		methods:{
			showContent:function(i){
				this.isBind=i
				var name = '';
				if(i==0){
					name="选择类目";
					this.isClassify = 0;
				}
				if(i==1){
					name="是否选择模板";//
					this.chooseTemplate = 1;
				}
				if(i==2){
					name="搜索、获取货品模板";//
					this.searchTemplate = 1;
				}
				if(i==3){
					name="填写商品信息";//
					this.goodsInfo = 1;
				}
				if(i==4){
					name="审核";
					this.PersonOrCom = 1;
				}
				if(i==5){
					name="机器审核";
					this.comCheck = 1;
				}
				if(i==6){
					name="人工审核";
					this.personCheck = 1;
				}
				this.$emit("show",i,name);
			}
		}
	}
</script>
